// import React from 'react'
// import TodoItem from './conponents/TodoItem'
// function App() {
//   const TodoBase= [{
    
//   }]
//   return (
//     [1,2,3,4].map(todo => <TodoItem/>)
//   )
// }

// export default App
import React, { useState } from 'react';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [category, setCategory] = useState('business');
  const [task, setTask] = useState('');

  const addTodo = () => {
    if (task.trim() !== '') {
      const newItem = {
        id: Date.now(),
        task: task,
        category: category,
        complete: false,
      };
      setTodos([...todos, newItem]);
      setTask('');
    }
  };

  const deleteTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const toggleComplete = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, complete: !todo.complete } : todo
    );
    setTodos(updatedTodos);
  };

  const changeCategory = (id, newCategory) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, category: newCategory } : todo
    );
    setTodos(updatedTodos);
  };

  return (
    <div>
      <h1>Todo List</h1>
      <div>
        <label>
          <input
            type="radio"
            value="business"
            checked={category === 'business'}
            onChange={() => setCategory('business')}
          />
          Business
        </label>
        <label>
          <input
            type="radio"
            value="personal"
            checked={category === 'personal'}
            onChange={() => setCategory('personal')}
          />
          Personal
        </label>
      </div>
      <div>
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Введите задачу"
        />
        <button onClick={addTodo}>Добавить</button>
      </div>
      <div>
        <h2>Список задач</h2>
        {todos.map((todo) => (
          <div key={todo.id}>
            <span
              style={{
                textDecoration: todo.complete ? 'line-through' : 'none',
                marginRight: '8px',
              }}
            >
              {todo.task} - {todo.category}
            </span>
            <input
              type="radio"
              value="business"
              checked={todo.category === 'business'}
              onChange={() => changeCategory(todo.id, 'business')}
            />
            Business
            <input
              type="radio"
              value="personal"
              checked={todo.category === 'personal'}
              onChange={() => changeCategory(todo.id, 'personal')}
            />
            Personal
            <button onClick={() => toggleComplete(todo.id)}>
              {todo.complete ? 'Отменить выполнение' : 'Выполнить'}
            </button>
            <button onClick={() => deleteTodo(todo.id)}>Удалить</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
