// import React from 'react'
// import TodoItem from './conponents/TodoItem'
// function App() {
//   const TodoBase= [{
    
//   }]
//   return (
//     [1,2,3,4].map(todo => <TodoItem/>)
//   )
// }

// export default App
import React, { useState } from 'react';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [categoryBusiness, setCategoryBusiness] = useState(false);
  const [categoryPersonal, setCategoryPersonal] = useState(false);
  const [task, setTask] = useState('');

  const addTodo = () => {
    if (task.trim() !== '') {
      const newItem = {
        id: Date.now(),
        task: task,
        categories: {
          business: categoryBusiness,
          personal: categoryPersonal,
        },
        complete: false,
      };
      setTodos([...todos, newItem]);
      setTask('');
    }
  };

  const deleteTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const toggleComplete = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, complete: !todo.complete } : todo
    );
    setTodos(updatedTodos);
  };

  return (
    <div>
      <h1>Todo List</h1>
      <div>
        <label>
          Категория "Business":
          <input
            type="checkbox"
            checked={categoryBusiness}
            onChange={() => setCategoryBusiness(!categoryBusiness)}
          />
        </label>
      </div>
      <div>
        <label>
          Категория "Personal":
          <input
            type="checkbox"
            checked={categoryPersonal}
            onChange={() => setCategoryPersonal(!categoryPersonal)}
          />
        </label>
      </div>
      <div>
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Введите задачу"
        />
        <button onClick={addTodo}>Добавить</button>
      </div>
      <div>
        <h2>Список задач</h2>
        {todos.map((todo) => (
          <div key={todo.id}>
            <span
              style={{
                textDecoration: todo.complete ? 'line-through' : 'none',
                marginRight: '8px',
              }}
            >
              {todo.task} - {todo.categories.business ? 'Business ' : ''}{todo.categories.personal ? 'Personal' : ''}
            </span>
            <button onClick={() => toggleComplete(todo.id)}>
              {todo.complete ? 'Отменить выполнение' : 'Выполнить'}
            </button>
            <button onClick={() => deleteTodo(todo.id)}>Удалить</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
